import '../css/maze.css'
import Grid from './grid.js'

function Maze() {
    var no_rows=new Array(50).fill(true)
    var no_columns=new Array(75).fill(true)
    var maze_width="100px"
    var grid=document.getElementsByClassName('grid');
    grid.style.width='10px'
    grid.style.height='10px'
    return (
        <div className="maze" max-width={maze_width}>
           {no_rows.map(()=>
                no_columns.map(()=>
                    <Grid />
                )
            )}
        </div>
    );
};

export default Maze;
