import '../css/maze.css'
import Grid from './grid.js'

function Maze() {
    var no_rows=new Array(10).fill(true)
    var no_columns=new Array(10).fill(true)
    var maze_width="100px"
    return (
        <div className="maze" max-width={maze_width}>
           {no_rows.map(()=>
                no_columns.map(()=>
                    <Grid height="10px" width="10px" />
                )
            )}
        </div>
    );
};

export default Maze;
